mapper.insert:
INSERT	INTO MEMBER (
	ID
	, PASSWORD
	, NAME
	, NICKNAME
	, EMAIL
	, REGISTRATION_DATE
	, GRADE
	, ACCESS_COUNT

)
VALUES (
	#{id}
	, #{password}
	, #{name}
	, #{nickname}
	, #{email}
	, SYSDATE
	, 1
	, 1
)

mapper.select:

SELECT	*
FROM	MEMBER
WHERE	PASSWORD = #{password}
AND		ID = #{id}

SELECT	COUNT(*)
FROM	MEMBER
WHERE	ID = #{id}

SELECT	COUNT(*)
FROM	MEMBER
WHERE	NICKNAME = #{nickname}

SELECT	COUNT(*)
FROM	MEMBER
WHERE	EMAIL = #{email}

SELECT	PASSWORD
FROM	MEMBER
WHERE	EMAIL = #{email}
